package mobius.cct.classfile;

import java.io.IOException;
import java.io.InputStream;

/**
 * Interface of objects capable of creating ClassFile instances
 * from input streams.
 * @param <T> Type of class files generated by this reader.
 * @author Tadeusz Sznuk (ts209501@gmail.com)
 */
public interface ClassReader<T extends ClassFile> {
  /**
   * Read class from given input stream.
   * @param is Input stream.
   * @return ClassFile object.
   * @throws IOException if it is thrown during reading from the stream.
   */
  T read(InputStream is) throws IOException;
}
