package mobius.prover.exec.toplevel.exceptions;

import mobius.prover.Prover;
import mobius.prover.plugins.exceptions.ProverException;


/**
 * A generic exception generated by the top level.
 */
public class ToplevelException extends ProverException {
	/** A serial UID to have much more fun... */
	private static final long serialVersionUID = 1L;

	/**
	 * Create a new top level exception.
	 * @param pkind The prover originating the exception
	 * @param description A description of the error
	 */
	public ToplevelException(Prover pkind, String description) {
		super(pkind.getName() +".TopLevel", description);
	}

}
